DO $$
DECLARE
    social_media_category_id VARCHAR(50);
BEGIN
    -- Get the social media category ID
    SELECT id INTO social_media_category_id FROM categories WHERE id = 'social-media';
    
    -- If category doesn't exist, create it
    IF social_media_category_id IS NULL THEN
        VALUES ('social-media', 'Social Media', 'AI tools for social media management, content creation, and analytics', 'ðŸ“±', 'from-blue-500 to-indigo-500', NOW(), NOW());
        social_media_category_id := 'social-media';
    END IF;    -- Insert 50 new social media AI tools

    RAISE NOTICE 'Successfully added 50 new AI tools to the Social Media category (ID: %)', social_media_category_id;
